  },
  methods: {
    cancel() {
      return modalController.dismiss('cancel', this.videoInfo);
    },
    takeVideo: async function () {
      try {
        const options = {
          duration: 4,
          limit: 1,
          quality: 1
        };
        const result = await MediaCapture.captureVideo(options);
        this.videoInfo = result[0];
        // console.log("videoInfo", JSON.stringify(result[0]));
        const blob = await fetch(Capacitor.convertFileSrc(this.videoInfo.fullPath)).then(r => r.blob());
        const formData = new FormData();
        formData.append("file", blob, this.videoInfo.name);
        // convert video blob to base64
        let reader = new FileReader();
        reader.readAsDataURL(blob);
        reader.onloadend = async function () {
          let base64data = reader.result;
          let trimmed = base64data.substring(base64data.indexOf(",") + 1);
          console.log(trimmed); // DEBUG
          const loading = await loadingController.create({
            message: 'Please wait...'
          });
          await loading.present();
          axios({
            method: "post",
            url: "https://e3a7-209-107-120-235.ngrok.io/upload/",
            data: JSON.stringify({
              _data: trimmed
            }),
            headers: {
              "Content-Type": "application/json"
            }
          }).then(response => {
            loading.dismiss();
            if (response.status == 200) {
              alert(JSON.stringify(response.data));
              document.getElementById("modalButton").click();
            }
          });
        };
      } catch (error) {
        // NEVER PUT ALERT HERE
        console.log(error);
      }
    }
  }
